namespace SpriteKind {
    export const home = SpriteKind.create()
    export const debris = SpriteKind.create()
}
/**
 * City generated by selecting 5 buildings from an array
 * 
 * Ship locked to only x movement
 * 
 * Score- point deducted for every launch- 3 points for comet destroyed
 * 
 * Health- 10 lost for each building destroyed or you ship being disabled
 * 
 * Ship when hit will land and can still fire torpedo
 * 
 * Lose when score is 0
 * 
 * Neopixels light up with 5 greens at start and turn to red as bases destroyed.
 */
controller.A.onEvent(ControllerButtonEvent.Pressed, function () {
    info.changeScoreBy(-1)
    torpedo = sprites.create(img`
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        `, SpriteKind.Projectile)
    music.pewPew.play()
    torpedo.setVelocity(0, -50)
    torpedo.setPosition(ship.x, ship.y)
    torpedo.setFlag(SpriteFlag.AutoDestroy, true)
})
sprites.onOverlap(SpriteKind.Enemy, SpriteKind.Player, function (sprite, otherSprite) {
    sprite.setVelocity(0, 0)
    sprite.destroy(effects.disintegrate, 2000)
    ship.vy += 10
    controller.moveSprite(ship, 0, 0)
    info.changeLifeBy(-10)
})
info.onLifeZero(function () {
    game.over(false)
})
sprites.onOverlap(SpriteKind.Enemy, SpriteKind.home, function (sprite, otherSprite) {
    sprite.setVelocity(0, 2)
    music.baDing.play()
    sprite.destroy(effects.disintegrate, 2000)
    otherSprite.destroy(effects.disintegrate, 2000)
    ruin = sprites.create(img`
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        . . . . . . . . . . . . . . . . 
        `, SpriteKind.debris)
    ruin.setPosition(otherSprite.x, otherSprite.y)
    info.changeLifeBy(-10)
    bases += -1
    light.setPixelColor(bases, 0xff0000)
})
sprites.onOverlap(SpriteKind.Projectile, SpriteKind.Enemy, function (sprite, otherSprite) {
    info.changeScoreBy(3)
    otherSprite.destroy(effects.disintegrate, 200)
    sprite.destroy(effects.fire, 100)
})
